import React, { Component } from "react";
import Slide from "react-reveal";
import validator from "validator";
import Select from "react-select";
import emailjs from "emailjs-com";
import axios from "axios";
import Calendar from "react-calendar";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

const options = [
  { value: "beginner", label: "Beginner" },
  { value: "intermediate", label: "Intermediate" },
  { value: "Expert", label: "Expert" },
];

var rand = Math.random().toString(36).split(".")[1].slice(0, 8) + "";

let URL_Interview = "https://jsonplaceholder.typicode.com/users";

class Apply extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      educationCollege: "",
      educationCourse: "",
      educationBranch: "",
      educationYear: "",
      personalInfo: "",
      isCollegeProject: false,
      projects_outside: "",
      isHardware: "",
      git_url: "",
      video_software_url: "",
      video_url: "",
      linkedIn_url: "",
      about_testing: "",
      start_testing: "",
      programming_level: null,
      programming_language: "",
      how_u_find_us: "",
      technical_interest: "",
      looking_from_us: "",
      isEnabled: false,
      Year: new Date(),
    };

    this.checks = {
      check1: false,
      check2: false,
      check3: false,
      check4: false,
      check5: false,
    };

    this.email_error = "";
    this.year_error = "";

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.onRadioChange = this.onRadioChange.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSelectChange = this.handleSelectChange.bind(this);
  }

  handleSelectChange = (programming_level) => {
    this.setState({ programming_level });
    console.log(this.state.programming_level);
    this.setButton();
  };

  handleInputChange = (event) => {
    const id = event.target.id;

    switch (id) {
      case "check1":
        this.checks.check1 = !this.checks.check1;
        break;
      case "check2":
        this.checks.check2 = !this.checks.check2;
        break;
      case "check3":
        this.checks.check3 = !this.checks.check3;
        break;
      case "check4":
        this.checks.check4 = !this.checks.check4;
        break;
      case "check5":
        this.checks.check5 = !this.checks.check5;
        break;
      default:
        break;
    }
    this.setButton();
  };

  handleChange = (event) => {
    const role = event.target.id;

    switch (role) {
      case "Name":
        this.setState({ name: event.target.value });
        break;
      case "email":
        if (validator.isEmail(event.target.value)) {
          this.email_error = "";
          this.setState({ email: event.target.value });
        } else {
          this.email_error = "Enter valid Email!";
        }
        break;
      case "education_college":
        this.setState({ educationCollege: event.target.value });
        break;
      case "education_course":
        this.setState({ educationCourse: event.target.value });
        break;
      case "education_branch":
        this.setState({ educationBranch: event.target.value });
        break;
      case "education_year":
        let value = event.target.value;
        if (value < 2030) {
          this.year_error = "";
          this.setState({ educationYear: event.target.value });
        } else {
          this.year_error = "Enter valid Year in format YYYY and should ";
        }
        break;
      case "personalInfo":
        this.setState({ personalInfo: event.target.value });
        break;
      case "projects_outside":
        this.setState({ projects_outside: event.target.value });
        break;
      case "github":
        this.setState({ git_url: event.target.value });
        break;
      case "video_software":
        this.setState({ video_software_url: event.target.value });
        break;
      case "video":
        this.setState({ video_url: event.target.value });
        break;
      case "linkedIn":
        this.setState({ linkedIn_url: event.target.value });
        break;
      case "testing-knowledge":
        this.setState({ about_testing: event.target.value });
        break;
      case "software-testing":
        this.setState({ start_testing: event.target.value });
        break;
      case "programming_level":
        this.setState({ programming_level: event.target.value });
        break;
      case "languages":
        this.setState({ programming_language: event.target.value });
        break;
      case "find-us":
        this.setState({ how_u_find_us: event.target.value });
        break;
      case "looking-for":
        this.setState({ looking_from_us: event.target.value });
        break;
      case "technical-interest":
        this.setState({ technical_interest: event.target.value });
        break;
      default:
        break;
    }
    this.setButton();
  };

  setButton() {
    if (
      this.state.name.length > 3 &&
      this.state.email.length > 3 &&
      this.state.educationCollege.length > 3 &&
      this.state.educationCourse.length > 3 &&
      this.state.educationBranch.length > 3 &&
      this.state.educationYear.length === 4 &&
      this.state.personalInfo.length > 3 &&
      this.state.linkedIn_url.length > 3 &&
      this.state.start_testing.length > 3 &&
      this.state.programming_language.length > 3 &&
      this.state.programming_level != null &&
      this.state.how_u_find_us.length > 3 &&
      this.state.technical_interest.length > 3 &&
      this.state.looking_from_us.length > 3 &&
      this.checks.check1 &&
      this.checks.check2 &&
      this.checks.check3 &&
      this.checks.check4 &&
      this.checks.check5
    ) {
      this.setState({ isEnabled: true });
      console.log("All set");
    } else {
      this.setState({ isEnabled: false });
    }
  }

  onRadioChange = (event) => {
    const name = event.target.name;
    const val = event.target.value;

    console.log(name, val);
    switch (name) {
      case "college-projects":
        if (val === "yes") {
          this.setState({ isCollegeProject: true });
        } else {
          this.setState({ isCollegeProject: false });
        }
        break;
      case "project-type":
        if (val === "hardware") {
          this.setState({ isHardware: true });
        } else {
          this.setState({ isHardware: false });
        }
        break;
      default:
        break;
    }
    this.setButton();
  };

  handleSubmit = (event) => {
    let Answer = {
      "Q1 Name": this.state.name,
      "Q2 Email": this.state.email,
      "Q3.a college ": this.state.educationCollege,
      "Q3.b course ": this.state.educationCourse,
      "Q3.c branch ": this.state.educationBranch,
      "Q3.d year of pass out ": this.state.educationYear,
      "Q4 A little bit about yourself": this.state.personalInfo,
      "Q5 Have you done any projects outside of college?": this.state
        .isCollegeProject
        ? "Yes"
        : "No",
      "Q5.1 What projects have you done outside of college?":
        this.state.projects_outside,
      "Q5.2 Hardware or software radio button": this.state.isHardware
        ? "Hardware"
        : "Software",
      "Q5.2.a (if software) GitHub profile": this.state.git_url,
      "Q5.2.b (if software) Link to a Video of a working software project":
        this.state.video_software_url,
      "Q5.2.a (if hardware) Link to a Video of a working hardware project":
        this.state.video_url,
      "Q6 LinkedIn profile": this.state.linkedIn_url,
      "Q7 What do you know about testing?": this.state.about_testing,
      "Q8 Would you like to start your career in software testing?":
        this.state.start_testing,
      "Q9 Level of programming": this.state.programming_language.label,
      "Q9.1 What languages do you code in?": this.state.programming_language,
      "Q10 How did you find out about us?": this.state.how_u_find_us,
      "Q11 What are you looking for from this program?":
        this.state.looking_from_us,
      "Q12 What are your technical interests?": this.state.technical_interest,
    };
    event.preventDefault();

    console.log(Answer);
    let service_id = "default_service";
    let template_id = "careers_application";
    let user_id = "user_sLVwc5nbsxF2PpYyoi7vz";
    let template_params = {
      contact_number: rand,
      answers: Answer,
    };

    emailjs.sendForm(service_id, template_id, template_params, user_id).then(
      (result) => {
        console.log(result.text);
      },
      (error) => {
        console.log(error.text);
      }
    );

    // For getting the API response
    axios.get(URL_Interview).then((res) => {
      const persons = res.data;
      this.setState({ persons });
    });
  };

  componentDidMount() {
    console.log("Find a bug and the course price is free");
    // For getting the API response
    axios.get(URL_Interview).then((res) => {
      const persons = res.data;
      this.setState({ persons });
    });
  }

  render() {
    const imp = <span className="required">*</span>;

    return (
      <section id="apply">
        <Slide left duration={1300}>
          <div className="row education">
            <div className="eight columns header-col">
              <h1>
                <span>Apply Here</span>
              </h1>
            </div>

            <div className="nine columns main-col">
              <div className="row item"></div>
            </div>
          </div>
        </Slide>

        <Slide left duration={1300}>
          <div className="row work">
            <div className="twelve columns header-col">
              <form onSubmit={this.handleSubmit}>
                <label>
                  Name {imp}
                  <input
                    id="Name"
                    className="input-text form-control form-control-lg"
                    type="text"
                    value={this.state.name}
                    onChange={this.handleChange}
                  />
                </label>

                <Calendar value={this.state.Year} formatYear />

                <DatePicker
                  selected={this.state.Year}
                  // onChange={handleDateChange}
                  showTimeSelect
                  dateFormat="yyyy"
                />

                <label>
                  Email {imp}
                  <input
                    id="email"
                    className="input-text"
                    type="text"
                    onChange={this.handleChange}
                  />
                  <span
                    style={{
                      fontWeight: "bold",
                      color: "red",
                    }}
                  >
                    {this.email_error}
                  </span>
                </label>

                <h4>Education: </h4>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    padding: 15,
                  }}
                >
                  <label>College{imp}</label>
                  <input
                    id="education_college"
                    className="input-text"
                    type="text"
                    value={this.state.educationCollege}
                    onChange={this.handleChange}
                  />

                  <label>Course{imp}</label>
                  <input
                    id="education_course"
                    className="input-text"
                    type="text"
                    value={this.state.educationCourse}
                    onChange={this.handleChange}
                  />

                  <label>Branch{imp}</label>
                  <input
                    id="education_branch"
                    className="input-text"
                    type="text"
                    value={this.state.educationBranch}
                    onChange={this.handleChange}
                  />

                  <label>year of pass out{imp}</label>
                  <input
                    id="education_year"
                    className="input-number"
                    placeholder={2021}
                    type="number"
                    value={this.state.educationYear}
                    onChange={this.handleChange}
                  />
                  <span
                    style={{
                      fontWeight: "bold",
                      color: "red",
                    }}
                  >
                    {this.year_error}
                  </span>
                </div>

                <label>
                  A little bit about yourself {imp}
                  <textarea
                    id="personalInfo"
                    className="input-text"
                    type="text"
                    value={this.state.personalInfo}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  Have you done any projects outside of college? {imp}
                  <br />
                  <div onChange={this.onRadioChange}>
                    <input type="radio" value="yes" name="college-projects" />
                    Yes
                    <br />
                    <input type="radio" value="no" name="college-projects" /> No
                  </div>
                </label>
                {this.state.isCollegeProject && (
                  <div>
                    <label>
                      What projects have you done outside of college? {imp}
                      <textarea
                        id="projects_outside"
                        className="input-text"
                        type="text"
                        value={this.state.projectOutside}
                        onChange={this.handleChange}
                      />
                    </label>

                    <label>
                      what is your project related to? {imp}
                      <br />
                      <div onChange={this.onRadioChange}>
                        <input
                          type="radio"
                          value="hardware"
                          name="project-type"
                        />
                        Hardware
                        <br />
                        <input
                          type="radio"
                          value="software"
                          name="project-type"
                        />
                        software
                      </div>
                    </label>
                    {!this.state.isHardware ? (
                      <div>
                        <label>
                          GitHub profile {imp}
                          <input
                            id="github"
                            className="input-text"
                            type="text"
                            value={this.state.git_url}
                            onChange={this.handleChange}
                          />
                        </label>

                        <label>
                          Link to a Video of a working software project {imp}
                          <input
                            id="video_software"
                            className="input-text"
                            type="text"
                            value={this.state.video_software_url}
                            onChange={this.handleChange}
                          />
                        </label>
                      </div>
                    ) : (
                      <label>
                        Link to a Video of a working hardware project {imp}
                        <input
                          id="video"
                          className="input-text"
                          type="text"
                          value={this.state.video_url}
                          onChange={this.handleChange}
                        />
                      </label>
                    )}
                  </div>
                )}

                <label>
                  LinkedIn profile {imp}
                  <input
                    id="linkedIn"
                    className="input-text"
                    type="text"
                    value={this.state.linkedIn_url}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  What do you know about testing? {imp}
                  <textarea
                    id="testing-knowledge"
                    className="input-text"
                    type="text"
                    value={this.state.about_testing}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  Would you like to start your career in software testing? {imp}
                  <textarea
                    id="software-testing"
                    className="input-text"
                    type="text"
                    value={this.state.start_testing}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  Level of programming{imp}
                  <Select
                    isSearchable={false}
                    className="input-select"
                    value={this.state.programming_level}
                    onChange={this.handleSelectChange}
                    options={options}
                  />
                </label>

                {/* THis would depend on the level of programming */}
                <label>
                  What languages do you code in? {imp}
                  <input
                    id="languages"
                    className="input-text"
                    type="text"
                    value={this.state.programming_language}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  How did you find out about us? {imp}
                  <input
                    id="find-us"
                    className="input-text"
                    type="text"
                    value={this.state.how_u_find_us}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  What are you looking for from this program? {imp}
                  <input
                    id="looking-for"
                    className="input-text"
                    type="text"
                    value={this.state.looking_from_us}
                    onChange={this.handleChange}
                  />
                </label>

                <label>
                  What are your technical interests? {imp}
                  <input
                    id="technical-interest"
                    className="input-text"
                    type="text"
                    value={this.state.technical_interest}
                    onChange={this.handleChange}
                  />
                </label>

                <label>Do you understand that:</label>

                <div>
                  <label class="container">
                    fully remote{" "}
                    <input
                      type="checkbox"
                      id="check1"
                      onChange={this.handleInputChange}
                    />{" "}
                    {imp}
                    <span class="checkmark"></span>
                  </label>
                  <label class="container">
                    {" "}
                    not for everyone{" "}
                    <input
                      type="checkbox"
                      id="check2"
                      onChange={this.handleInputChange}
                    />{" "}
                    {imp}
                    <span class="checkmark"></span>
                  </label>
                  <label class="container">
                    {" "}
                    fast paced{" "}
                    <input
                      type="checkbox"
                      id="check3"
                      onChange={this.handleInputChange}
                    />{" "}
                    {imp}
                    <span class="checkmark"></span>
                  </label>
                  <label class="container">
                    {" "}
                    no placement guarantee{" "}
                    <input
                      type="checkbox"
                      id="check4"
                      onChange={this.handleInputChange}
                    />{" "}
                    {imp}
                    <span class="checkmark"></span>
                  </label>
                  <label class="container">
                    {" "}
                    no money back{" "}
                    <input
                      type="checkbox"
                      id="check5"
                      onChange={this.handleInputChange}
                    />{" "}
                    {imp}
                    <span class="checkmark"></span>
                  </label>
                </div>

                <input
                  style={{
                    backgroundColor: !this.state.isEnabled
                      ? "grey"
                      : "lightseagreen",
                    borderRadius: 5,
                    marginTop: 30,
                    width: 100,
                    padding: 4,
                  }}
                  disabled={!this.state.isEnabled}
                  type="submit"
                  value="submit"
                />
              </form>
            </div>
          </div>
        </Slide>
      </section>
    );
  }
}

export default Apply;
